package com.bayamp.training.array.programs;

import java.util.Arrays;


public class CharArrayProgram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char[] myChars= {'h','a','r','i','s','h'};
		
		int[] myNumbers = {10,2,3,4};
		
		String s1 = Arrays.toString(myChars);
		
		System.out.println("the string conversion of an array is :" + s1);
		
		
		String s2 = Arrays.toString(myNumbers);
		
		System.out.println("the string conversion of an array is :" + s2);

	}

}
